sections.css:
h1.sg,
h2.sg,
h3.sg,
h4.sg,
h5.sg {
  font-family: $font-display;
  font-weight: normal;
  font-style: normal;
  line-height: 1.2;
  margin: 1.414em 0 .5em;
  color: #444;
}

h1.sg {
  font-size: 3.157em;

  margin-top: .5em;
}

h2.sg {
  font-size: 2.369em;
}

h3.sg {
  font-size: 1.777em;
}

h4.sg {
  font-size: 1.333em;
}

h4.sg,
h5.sg {
  font-weight: 500;
}

/* TODO
:active - Active
:visited - Visited
*/

a.sg {
	font-family: "Poppins";
	display: inline;
	cursor: pointer;
	text-decoration: none;
	color: $default-action-color;
}

.btn {
	margin: 0;
	padding: 0;
	border: 0;
	padding: 10px 20px;
	font-family: $font-display;
	border-radius: 3px;
	color: $white-default;
	&.default {
		background: $default-action-color;
		&:hover {
			background: darken($default-action-color, $action-color-change);
		}
	}
	&.primary {
		background: $primary-action-color;
		&:hover {
			background: darken($primary-action-color, $action-color-change);
		}
	}
	&.success {
		background: $tertiary-color;
		&:hover {
			background: darken($tertiary-color, $action-color-change);
		}
	}
	&.outline {
		background: transparent;
		border:1px solid $primary-action-color;
		color: $primary-action-color;
	}
}

.btn {
 	&.btn-sm {
 		padding: 5px 10px;
 	}
 	&.btn-lg {
		padding: 20px 30px;
 	}
 	&.btn-xl {
		padding: 30px 50px;
 	}
 	&.btn-block {
 		display: block;
 		width: 100%;
 	}
}

label {
	display: block;
	font-family: $font-display;
}
input {
	font-family: $font-display;
	margin: 0;
	padding: 10px 10px; 
	border-radius: 2px;
	border: 1px solid $gray-silver;
	display: block;
	&:focus {
		outline: none;
		border-color: $primary-color;
	}
	&.block {
		width: 100%;
	}
	&.sm {
		padding: 5px 5px; 
	}
}

ul {
	&.nav {
		margin: 0;
		padding: 0;
		list-style: none;
		li {
			float: left;
			a {
				font-family: $font-display;
				display: block;
				margin-right: 10px;
				background: $gray-black;
				padding: 10px 20px;
				color: $white-default;
				border-radius: 3px;
				text-decoration: none;
				&:hover {
					background: $primary-action-color;
				}
				&.active {
					background: $primary-action-color;
				}
			}
		}
	}
}

.card {
	font-family: $font-display;
	width: 250px;
    padding-top: 20px;
    margin: 10px 0 20px 0;
    background-color: rgba(214, 224, 226, 0.2);
    border-top-width: 0;
    border-bottom-width: 2px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

.card .card-heading {
    padding: 0 20px;
    margin: 0;
}

.card .card-heading.simple {
    font-size: 20px;
    font-weight: 300;
    color: #777;
    border-bottom: 1px solid #e5e5e5;
}

.card .card-heading.image img {
    display: inline-block;
    width: 46px;
    height: 46px;
    margin-right: 15px;
    vertical-align: top;
    border: 0;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border-radius: 50%;
}

.card .card-heading.image .card-heading-header {
    display: inline-block;
    vertical-align: top;
}

.card .card-heading.image .card-heading-header h3 {
    margin: 0;
    font-size: 14px;
    line-height: 16px;
    color: #262626;
}

.card .card-heading.image .card-heading-header span {
    font-size: 12px;
    color: #999999;
}

.card .card-body {
    padding: 0 20px;
    margin-top: 20px;
}

.card .card-media {
    padding: 0 20px;
    margin: 0 -14px;
}

.card .card-media img {
    max-width: 100%;
    max-height: 100%;
}

.card .card-actions {
    min-height: 30px;
    padding: 0 20px 20px 20px;
    margin: 20px 0 0 0;
}

.card .card-comments {
    padding: 20px;
    margin: 0;
    background-color: #f8f8f8;
}

.card .card-comments .comments-collapse-toggle {
    padding: 0;
    margin: 0 20px 12px 20px;
}

.card .card-comments .comments-collapse-toggle a,
.card .card-comments .comments-collapse-toggle span {
    padding-right: 5px;
    overflow: hidden;
    font-size: 12px;
    color: #999;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.card-comments .media-heading {
    font-size: 13px;
    font-weight: bold;
}

.card.people {
    position: relative;
    display: inline-block;
    width: 170px;
    height: 300px;
    padding-top: 0;
    margin-left: 20px;
    overflow: hidden;
    vertical-align: top;
}

.card.people:first-child {
    margin-left: 0;
}

.card.people .card-top {
    position: absolute;
    top: 0;
    left: 0;
    display: inline-block;
    width: 170px;
    height: 150px;
    background-color: #ffffff;
}

.card.people .card-top.green {
    background-color: #53a93f;
}

.card.people .card-top.blue {
    background-color: #427fed;
}

.card.people .card-info {
    position: absolute;
    top: 150px;
    display: inline-block;
    width: 100%;
    height: 101px;
    overflow: hidden;
    background: #ffffff;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

.card.people .card-info .title {
    display: block;
    margin: 8px 14px 0 14px;
    overflow: hidden;
    font-size: 16px;
    font-weight: bold;
    line-height: 18px;
    color: #404040;
}

.card.people .card-info .desc {
    display: block;
    margin: 8px 14px 0 14px;
    overflow: hidden;
    font-size: 12px;
    line-height: 16px;
    color: #737373;
    text-overflow: ellipsis;
}

.card.people .card-bottom {
    position: absolute;
    bottom: 0;
    left: 0;
    display: inline-block;
    width: 100%;
    padding: 10px 20px;
    line-height: 29px;
    text-align: center;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

.card.hovercard {
    position: relative;
    padding-top: 0;
    overflow: hidden;
    text-align: center;
    background-color: rgba(214, 224, 226, 0.2);
}

.card.hovercard .cardheader {
    background: url("http://lorempixel.com/850/280/nature/4/");
    background-size: cover;
    height: 135px;
}

.card.hovercard .avatar {
    position: relative;
    top: -50px;
    margin-bottom: -50px;
}

.card.hovercard .avatar img {
    width: 100px;
    height: 100px;
    max-width: 100px;
    max-height: 100px;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border-radius: 50%;
    border: 5px solid rgba(255,255,255,0.5);
}

.card.hovercard .info {
    padding: 4px 8px 10px;
}

.card.hovercard .info .title {
    margin-bottom: 4px;
    font-size: 24px;
    line-height: 1;
    color: #262626;
    vertical-align: middle;
}

.card.hovercard .info .desc {
    overflow: hidden;
    font-size: 12px;
    line-height: 20px;
    color: #737373;
    text-overflow: ellipsis;
}

.card.hovercard .bottom {
    padding: 0 20px;
    margin-bottom: 17px;
}

.alert {
	padding: 10px 20px;
	margin: 0;
	border-radius: 3px;
	font-family: $font-display;
	p {
		margin: 0;
		padding: 0;
	}
	> .alert-header {
		font-size: 18px;
	}
	> .alert-body {
		font-size: 12px;
	}
	&.alert-info {
		@include alert($alert-info-color);
	}
	&.alert-success {
		@include alert($alert-success-color);
	}
	&.alert-warning {
		@include alert($alert-warning-color);
	}
	&.alert-danger {
		@include alert($alert-dagner-color);
	}

}

